import React, { useEffect, useState } from "react";
import { Link, useLocation } from "react-router-dom";
import Loader from "../Loader/Loader";
import WarningModal from "../WarningModal";
import Toast from "../Toast";
import { useContextHook } from "../../Providers/ContextProvider";

const StudentDetail = () => {
  const styles = {
    btn: {
      height: 20,
      width: 20,
      display: "flex",
      justifyContent: "center",
      alignItems: "center",
      borderRadius: 10,
    },
  };
  const [students, setStudents] = useState([]);
  const [loading, setLoading] = useState(true);
  const [modal, setModal] = useState(false);

  const [student, setStudent] = useState({
    name: "",
    surname: "",
    section: "",
  });
  const { showToast } = useContextHook();
  const location = useLocation();

  let name, value;

  const handleInput = (event) => {
    name = event.target.name;
    value = event.target.value;

    setStudent({ ...student, [name]: value });
  };

  const studentDetails = {
    name: student.name,
    surname: student.surname,
    section: student.section,
  };

  // const { name, surname, section } = route.params;

  // const { name, surname, section } = location.state;

  // console.log("Student details page--> ", name, surname, section);

  const getStudents = async (section) => {
    try {
      const res = await fetch("http://localhost:4000/get-all-students");
      const data = await res.json();

      if (data.status === 200) {
        if (section === "all") {
          setStudents(data.data);
          setLoading(false);
          return;
        }
        const filteredStudents = data.data.filter((item) => {
          console.log("Filter visited ");
          const { name, surname, section } = student;
          if (name && surname && section) {
            console.log("Filter visited 1 ");
            return (
              item.name.toLowerCase() === name.toLowerCase() &&
              item.surname.toLowerCase() === surname.toLowerCase() &&
              item.section.toLowerCase() === section.toLowerCase()
            );
          } else if (name && surname) {
            console.log("Filter visited 2 ");
            return (
              item.name.toLowerCase() === name.toLowerCase() &&
              item.surname.toLowerCase() === surname.toLowerCase()
            );
          } else if (name && section) {
            console.log("Filter visited 3");
            return (
              item.name.toLowerCase() === name.toLowerCase() &&
              item.section.toLowerCase() === section.toLowerCase()
            );
          } else if (name) {
            console.log("Filter visited 4");
            return item.name.toLowerCase() === name.toLowerCase();
          } else if (section) {
            console.log("Filter visited 5 ");
            return item.section.toLowerCase() === section.toLowerCase();
          }
        });
        setStudents(filteredStudents);
        setLoading(false);
      } else {
        setStudents(undefined);
        setLoading(false);
      }
    } catch (error) {
      console.log("Error white getting Students --> ", error);
      setLoading(false);
    }
  };

  console.log("students list after filter --> ", students);

  const searchStudents = () => {
    console.log("search students function called");
    if (!student.name && !student.surname && !student.section) {
      alert("Please select atleast one to search ");
      return;
    } else {
      getStudents();
    }
  };

  // if (loading) {
  //   return <Loader />;
  // }
  return (
    <div
      style={{
        display: "flex",
        flexDirection: "row",
        justifyContent: "space-around",
      }}
    >
      <div
        style={{
          display: "flex",
          position: "absolute",
          left: 0,
          height: "80vh",
          width: "30%",
          justifyContent: "flex-start",
        }}
        className="container mt-5"
      >
        <div className="signup-content">
          <div className="signup-form">
            <h2 className="form-title">Filters</h2>

            <form method="POST" className="register-form" id="register-from">
              <div className="form-group">
                <label className="label" htmlFor="name">
                  <i className="zmdi zmdi-account "></i>
                </label>
                <input
                  className="input-data"
                  type="text"
                  name="name"
                  id="name"
                  autoComplete="off"
                  value={student.name}
                  onChange={handleInput}
                  placeholder="Enter Name"
                />
              </div>

              <div className="form-group">
                <label className="label" htmlFor="surname">
                  <i className="zmdi zmdi-account "></i>
                </label>
                <input
                  className="input-data"
                  type="text"
                  name="surname"
                  id="surname"
                  autoComplete="off"
                  value={student.surname}
                  onChange={handleInput}
                  placeholder="Enter Surname"
                />
              </div>

              <div className="form-group">
                <label className="label" htmlFor="section">
                  <i className="zmdi zmdi-font "></i>
                </label>
                <input
                  className="input-data"
                  type="text"
                  name="section"
                  id="section"
                  autoComplete="off"
                  value={student.section}
                  onChange={handleInput}
                  placeholder="Enter Class"
                />
              </div>

              <div className="form-group">
                <Link to={"/delete-detail"} state={studentDetails}>
                  <input
                    className=" form-submit"
                    type="submit"
                    name="signup"
                    id="signup"
                    value={"Search Student"}
                    disabled={
                      !student.name || !student.section || !student.surname
                    }
                    onClick={() => searchStudents()}
                  />
                </Link>
              </div>
            </form>
          </div>
        </div>
      </div>

      {/* students list table */}

      <div
        style={{ padding: 20, margin: 20, position: "absolute", left: "35%" }}
      >
        <Toast />
        {students.length > 0 ? (
          <table className="table table-hover">
            <thead>
              <tr>
                <th scope="col">S.No.</th>
                <th scope="col">Name</th>
                <th scope="col">Class</th>
                <th scope="col">Father</th>
                <th scope="col">Mother</th>
                <th scope="col">Action</th>
              </tr>
            </thead>
            {students.map((item, index) => {
              // console.log("MAP LOOP--> ", item.name);

              return (
                <tbody key={index}>
                  <tr>
                    <th scope="row">{index + 1}</th>
                    <td>
                      {item.name} {item.surname}
                    </td>
                    <td>{item.section}</td>
                    <td>{item.father}</td>
                    <td>{item.mother}</td>
                    <td>
                      <button
                        style={styles.btn}
                        onClick={() => setModal(true)}
                        className="btn btn-outline-danger"
                      >
                        X
                      </button>
                    </td>
                  </tr>
                </tbody>
              );
            })}
          </table>
        ) : (
          <h3>No Student Found by name in data.</h3>
        )}
        {modal && (
          <WarningModal
            message={`Do you really want to delete ${name} from school?`}
            setState={setModal}
            method={() => console.log("deleted successfully")}
            showToast={showToast}
          />
        )}
        <Toast />
      </div>
    </div>
  );
};

export default StudentDetail;
