import React, { useEffect, useState } from "react";
import { useContextHook } from "../../Providers/ContextProvider";

const DisplayTeachers = () => {
  const [teachers, setTeachers] = useState([]);
  const { title } = useContextHook();

  const styles = {
    mainContainer
  }
  const getTeachers = async () => {
    const response = await fetch("http://localhost:4000/get-all-teachers");
    const data = await response.json();
    if (data.status === 200) {
      setTeachers(data.data);
    } else {
      setTeachers(undefined);
    }
  };
  useEffect(() => {
    getTeachers();
  }, []);

  const deleteTeacher = async (email) => {
    try {
      const res = await fetch("http://localhost:4000/delete-teacher", {
        method: "DELETE",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          email,
        }),
      });
      console.log("Delete Console", res.status);
      if (res.status === 200) {
        getTeachers();
      }
      // if (res.status === 200) {
      //   console.log("Teacher deleted successfully.");
      // } else {
      //   console.error("Failed to delete teacher.");
      // }
    } catch (error) {}
  };
  return (
    <>
      <div
        style={styles.mainContainer}
      >
        {teachers.length > 0 &&
          teachers.map((item, index) => {
            return (
              <div
                style={{
                  display: "flex",
                  flexDirection: "column",
                  justifyContent: "center",
                  alignItems: "center",
                  padding: 20,
                  margin: 10,
                  border: "1px solid black",
                  borderRadius: 20,
                  backgroundColor: "#42CFDA",
                }}
                key={index}
              >
                <h5>
                  {item.fName} {item.lName}
                </h5>
                <h5>{item.designation}</h5>
                <h5>{item.phone}</h5>
                <button
                  onClick={() => deleteTeacher(item.email)}
                  style={{
                    alignSelf: "center",
                    borderRadius: 10,
                    justifyContent: "center",
                    alignItems: "center",
                    display: "flex",
                    marginTop: 20,
                  }}
                  className="btn btn-outline-danger"
                >
                  delete
                </button>
              </div>
            );
          })}
      </div>
    </>
  );
};

export default DisplayTeachers;
