import React, { useState } from "react";

const Toast = () => {
  const [isVisible, setIsVisible] = useState(true);

  setTimeout(() => setIsVisible(false), 5000);

  if (!isVisible) {
    return null;
  }

  return (
    <div>
      <h1>Toart is visible </h1>
    </div>
  );
};

export default Toast;
