import React, { useState } from "react";
import { useContextHook } from "../Providers/ContextProvider";

const Toast = () => {
  const { toastVisible, toastMsg, setToastVisible } = useContextHook();

  const styles = {
    container: {
      display: "flex",
      position: "absolute",
      top: 0,
      left: "30%",
      right: "30%",
    },
  };

  // setTimeout(() => setToastVisible(false), 5000);

  if (!toastVisible) {
    return null;
  }

  return (
    <div style={styles.container}>
      <h1>Toart is visible </h1>
    </div>
  );
};

export default Toast;
