import React, { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";

import ".././Styles/Signup.css";

const AddStudent = () => {
  const navigate = useNavigate();

  const [student, setStudent] = useState({
    name: "",
    surname: "",
    section: "",
    father: "",
    mother: "",
    adhaar: "",
    phone: "",
    sssm: "",
    bank: "",
    ifsc: "",
  });

  let name, value;

  const handleInput = (event) => {
    name = event.target.name;
    value = event.target.value;

    setStudent({ ...user, [name]: value });
  };

  const PostData = async (event) => {
    event.preventDefault();
    const {
      name,
      surname,
      section,
      father,
      mother,
      adhaar,
      phone,
      sssm,
      bank,
      ifsc,
    } = student;

    if (
      !name ||
      !surname ||
      !section ||
      !father ||
      !mother ||
      !adhaar ||
      !phone ||
      !sssm ||
      !bank ||
      !ifsc
    ) {
      alert("All fields required !!");
      return;
    }

    const res = await fetch(`http://localhost:4000/add-student`, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        name,
        surname,
        section,
        father,
        mother,
        adhaar,
        phone,
        sssm,
        bank,
        ifsc,
      }),
    });

    const data = await res.json();

    if (res.status === 422 || !data) {
      window.alert("User already exist!!");
      console.log(
        "Student already exist!!"
      );
    } else {
      window.alert("Student Added successfully now please Login");
      console.log("User registered successfully now please Login");
      //   navigate("/login");
    }
  };

  return (
    <>
      <section className="signup">
        <div className="container mt-5">
          <div className="signup-content">
            <div className="signup-form">
              <h2 className="form-title">Add New Teacher</h2>
              <form method="POST" className="register-form" id="register-from">
                <div className="form-group">
                  <label className="label" htmlFor="name">
                    <i className="zmdi zmdi-account "></i>
                  </label>
                  <input
                    className="input-data"
                    type="text"
                    name="fName"
                    id="fName"
                    autoComplete="off"
                    value={user.fName}
                    onChange={handleInput}
                    placeholder="Enter Name"
                  />
                </div>

                <div className="form-group">
                  <label className="label" htmlFor="name">
                    <i className="zmdi zmdi-account "></i>
                  </label>
                  <input
                    className="input-data"
                    type="text"
                    name="lName"
                    id="lName"
                    autoComplete="off"
                    value={user.lName}
                    onChange={handleInput}
                    placeholder="Enter Surname"
                  />
                </div>

                <div className="form-group">
                  <label className="label" htmlFor="email">
                    <i className="zmdi zmdi-email "></i>
                  </label>
                  <input
                    className="input-data"
                    type="email"
                    name="email"
                    id="email"
                    autoComplete="off"
                    value={user.email}
                    onChange={handleInput}
                    placeholder="Enter Email"
                  />
                </div>

                <div className="form-group">
                  <label className="label" htmlFor="phone">
                    <i className="zmdi zmdi-phone-in-talk "></i>
                  </label>
                  <input
                    className="input-data"
                    type="number"
                    name="phone"
                    id="phone"
                    autoComplete="off"
                    value={user.phone}
                    onChange={handleInput}
                    placeholder="Enter Phone "
                  />
                </div>

                <div className="form-group">
                  <label className="label" htmlFor="work">
                    <i className="zmdi zmdi-slideshow "></i>
                  </label>
                  <input
                    className="input-data"
                    type="text"
                    name="designation"
                    id="designation"
                    autoComplete="off"
                    value={user.designation}
                    onChange={handleInput}
                    placeholder="Enter Designation"
                  />
                </div>

                <div className="form-group">
                  <label className="label" htmlFor="password">
                    <i className="zmdi zmdi-key "></i>
                  </label>
                  <input
                    className="input-data"
                    type="password"
                    name="password"
                    id="password"
                    autoComplete="off"
                    value={user.password}
                    onChange={handleInput}
                    placeholder="Enter Password"
                  />
                </div>

                <div className="form-group">
                  <label className="label" htmlFor="confirm-password">
                    <i className="zmdi zmdi-lock "></i>
                  </label>
                  <input
                    className="input-data"
                    type="password"
                    name="cpassword"
                    id="cpassword"
                    autoComplete="off"
                    value={user.cpassword}
                    onChange={handleInput}
                    placeholder="Enter confirm-password"
                  />
                </div>
                <div className="form-group">
                  <input
                    className=" form-submit"
                    type="submit"
                    name="signup"
                    id="signup"
                    value={"Register"}
                    onClick={PostData}
                  />
                </div>
              </form>
            </div>

            {/* <div className="signup-image">
              <figure>
                <img
                  src={signupPic}
                  height={"300px"}
                  width={"300px"}
                  alt="registrationPic"
                />
              </figure>
              <Link to="/login" className="signup-page-link ">
                Already registered ? / (Login)
              </Link>
            </div> */}
          </div>
        </div>
      </section>
    </>
  );
};

export default AddStudent;
