import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";

const DisplayStudents = () => {
  const [students, setStudents] = useState();
  const { standard } = useParams();
  console.log(standard);

  const getStudents = async (section) => {
    try {
      const res = await fetch("http://localhost:4000/get-all-students");
      const data = await res.json();

      if (data.status === 200) {
        if (section === "all") {
          setStudents(data.data);
          return;
        }
        const filteredStudents = data.data.filter(
          (item) => item.section === section
        );
        setStudents([filteredStudents]);
      } else {
        setStudents(undefined);
      }
    } catch (error) {
      console.log("Error white getting Students --> ", error);
    }
  };

  useEffect(() => {
    getStudents(standard);
  }, []);

  return (
    <>
      {students.length > 0 && (
        <table class="table table-hover">
          <thead>
            <tr>
              <th scope="col">S.No.</th>
              <th scope="col">First</th>
              <th scope="col">Last</th>
              <th scope="col">Handle</th>
            </tr>
          </thead>
          <tbody>
            <tr>
              <th scope="row">1</th>
              <td>Mark</td>
              <td>Otto</td>
              <td>@mdo</td>
            </tr>
            <tr>
              <th scope="row">2</th>
              <td>Jacob</td>
              <td>Thornton</td>
              <td>@fat</td>
            </tr>
            <tr>
              <th scope="row">3</th>
              <td colspan="2">Larry the Bird</td>
              <td>@twitter</td>
            </tr>
          </tbody>
        </table>
      )}
    </>
  );
};

export default DisplayStudents;
