import React, { useEffect, useState } from "react";
import { useLocation } from "react-router-dom";
import Loader from "../Loader/Loader";
import WarningModal from "../WarningModal";
import Toast from "../Toast";
import { useContextHook } from "../../Providers/ContextProvider";

const StudentDetail = () => {
  const styles = {
    btn: {
      height: 20,
      width: 20,
      display: "flex",
      justifyContent: "center",
      alignItems: "center",
      borderRadius: 10,
    },
  };
  const [students, setStudents] = useState([]);
  const [loading, setLoading] = useState(true);
  const [modal, setModal] = useState(false);

  const { showToast } = useContextHook();

  const location = useLocation();

  // const { name, surname, section } = route.params;

  const { name, surname, section } = location.state;

  console.log("Student details page--> ", name, surname, section);

  const getStudents = async (section) => {
    try {
      const res = await fetch("http://localhost:4000/get-all-students");
      const data = await res.json();

      if (data.status === 200) {
        if (section === "all") {
          setStudents(data.data);
          setLoading(false);
          return;
        }
        const filteredStudents = data.data.filter((item) => {
          if (
            item.name.toLowerCase() === name.toLowerCase() &&
            item.surname.toLowerCase() === surname.toLowerCase()
          ) {
            return item.section === section;
          }
        });
        setStudents(filteredStudents);
        setLoading(false);
      } else {
        setStudents(undefined);
        setLoading(false);
      }
    } catch (error) {
      console.log("Error white getting Students --> ", error);
      setLoading(false);
    }
  };

  console.log("students list after filter --> ", students);

  useEffect(() => {
    getStudents(section);
  }, [name, surname, section]);

  if (loading) {
    return <Loader />;
  }
  return (
    <div style={{ padding: 20, margin: 20 }}>
      <Toast />
      {students.length > 0 ? (
        <table className="table table-hover">
          <thead>
            <tr>
              <th scope="col">S.No.</th>
              <th scope="col">Name</th>
              <th scope="col">Class</th>
              <th scope="col">Father</th>
              <th scope="col">Mother</th>
              <th scope="col">Contact</th>
              <th scope="col">Adhaar</th>
              <th scope="col">SSSM ID</th>
              <th scope="col">Bank</th>
              <th scope="col">IFSC</th>
              <th scope="col">Action</th>
            </tr>
          </thead>
          {students.map((item, index) => {
            // console.log("MAP LOOP--> ", item.name);

            return (
              <tbody key={index}>
                <tr>
                  <th scope="row">{index + 1}</th>
                  <td>
                    {item.name} {item.surname}
                  </td>
                  <td>{item.section}</td>
                  <td>{item.father}</td>
                  <td>{item.mother}</td>
                  <td>{item.phone}</td>
                  <td>{item.adhaar}</td>
                  <td>{item.sssm}</td>
                  <td>{item.bank}</td>
                  <td>{item.ifsc}</td>
                  <td>
                    <button
                      style={styles.btn}
                      onClick={() => setModal(true)}
                      className="btn btn-outline-danger"
                    >
                      X
                    </button>
                  </td>
                </tr>
              </tbody>
            );
          })}
        </table>
      ) : (
        <h1>
          No Student Found by name {name} {surname} {section} in data.
        </h1>
      )}
      {modal && (
        <WarningModal
          message={`Do you really want to delete ${name} from school?`}
          setState={setModal}
          method={() => console.log("deleted successfully")}
        />
      )}
      <Toast />
    </div>
  );
};

export default StudentDetail;
